--Database Aerooking, schema public (Default on pgAdmin)

DROP SCHEMA IF EXISTS public CASCADE;
CREATE SCHEMA IF NOT EXISTS public;

--Tables

--Administrateur

DROP TABLE IF EXISTS Administrateur CASCADE;
CREATE TABLE IF NOT EXISTS Administrateur (

    mailCompte VARCHAR(80) UNIQUE NOT NULL
);

--Aéroport

DROP TABLE IF EXISTS Aéroport CASCADE;
CREATE TABLE IF NOT EXISTS Aéroport(
    
    nom VARCHAR(80),
    nomVille VARCHAR(80),
	codePays VARCHAR(3),

    diminutif VARCHAR(3) UNIQUE NOT NULL,

    CONSTRAINT PK_Aéroport PRIMARY KEY (nomVille, nom, codePays)
);

--Avion

DROP TABLE IF EXISTS Avion CASCADE;
CREATE TABLE IF NOT EXISTS Avion (
    
    id SMALLSERIAL,
    nomCompagnie VARCHAR(80) NOT NULL,

    poidsMaxEnSoute INTEGER NOT NULL CHECK (poidsMaxEnSoute > 0),
    maxBagageAMain SMALLINT NOT NULL CHECK (maxBagageAMain >= 0),

    CONSTRAINT PK_Avion PRIMARY KEY (id)
);

--Classe

DROP TABLE IF EXISTS Classe CASCADE;
CREATE TABLE IF NOT EXISTS Classe (

    nom VARCHAR(80),

    supplément NUMERIC(8,2) NOT NULL CHECK (supplément >= 0),

    CONSTRAINT PK_Classe PRIMARY KEY (nom)
);

--Client

DROP TABLE IF EXISTS Client CASCADE;
CREATE TABLE IF NOT EXISTS Client (
    
    mailCompte VARCHAR(80) UNIQUE NOT NULL
);

--Compagnie

DROP TABLE IF EXISTS Compagnie CASCADE;
CREATE TABLE IF NOT EXISTS Compagnie (
    
    nom VARCHAR(80),

    CONSTRAINT PK_Compagnie PRIMARY KEY (nom)
);

--Compte

DROP TABLE IF EXISTS Compte CASCADE;
CREATE TABLE IF NOT EXISTS Compte (

    mail VARCHAR(80) UNIQUE NOT NULL,

    motDePasse VARCHAR(80) NOT NULL,

    CONSTRAINT PK_Compte PRIMARY KEY(mail)
);

--Pays

DROP TABLE IF EXISTS Pays CASCADE;
CREATE TABLE IF NOT EXISTS Pays (
   
    codeAlpha3 VARCHAR(3),
    
    nom VARCHAR(80) UNIQUE NOT NULL,

    CONSTRAINT PK_Pays PRIMARY KEY (codeAlpha3)
);

--Réservation

DROP TABLE IF EXISTS Réservation CASCADE;
CREATE TABLE IF NOT EXISTS Réservation (
    
    id SMALLSERIAL,
    mailClient VARCHAR(80) NOT NULL,

	dateEtHeureRéservation TIMESTAMP NOT NULL DEFAULT (CURRENT_DATE + CURRENT_TIME),
	nbBagagesEnSoute INT NOT NULL CHECK (nbBagagesEnSoute >= 0),
	nbBagagesAMain INT NOT NULL CHECK (nbBagagesAMain >= 0),
    allerRetour BOOLEAN NOT NULL,

    CONSTRAINT PK_Réservation PRIMARY KEY (id)
);

--Ville

DROP TABLE IF EXISTS Ville CASCADE;
CREATE TABLE IF NOT EXISTS Ville (
    
    nom VARCHAR(80),
    codePays VARCHAR(3),

    CONSTRAINT PK_Ville PRIMARY KEY (codePays, nom)
);

--Vol

DROP TABLE IF EXISTS Vol CASCADE;
CREATE TABLE IF NOT EXISTS Vol (
    
    id SMALLSERIAL,
    idAvion SMALLINT NOT NULL,
    nomAéroportDépart VARCHAR(80) NOT NULL,
	nomVilleAéroportDépart VARCHAR(80) NOT NULL,
	codePaysAéroportDépart VARCHAR(3) NOT NULL,
    nomAéroportArrivée VARCHAR(80) NOT NULL,
	nomVilleAéroportArrivée VARCHAR(80) NOT NULL,
	codePaysAéroportArrivée VARCHAR(3) NOT NULL,

    dateEtHeureDeDépart TIMESTAMP NOT NULL,
    dateEtHeureDArrivée TIMESTAMP NOT NULL CHECK (dateEtHeureDArrivée > dateEtHeureDeDépart),
    prix NUMERIC(8,2) NOT NULL CHECK (prix > 0),
    poidsMaxBagagesEnSoute SMALLINT NOT NULL CHECK (poidsMaxBagagesEnSoute > 0),
	
	CONSTRAINT CH_Vol 
		CHECK (
			(nomAéroportDépart, nomVilleAéroportDépart, codePaysAéroportDépart) <> 
			(nomAéroportArrivée, nomVilleAéroportArrivée, codePaysAéroportArrivée)
		),
    
    CONSTRAINT PK_Vol PRIMARY KEY (id)
);

--Voyageur

DROP TABLE IF EXISTS Voyageur CASCADE;
CREATE TABLE IF NOT EXISTS Voyageur (
    
    id SMALLSERIAL,

    nom VARCHAR(80) NOT NULL,
    prénom VARCHAR(80) NOT NULL,
    dateDeNaissance DATE NOT NULL,

    CONSTRAINT PK_Voyageur PRIMARY KEY (id)
);

--Tables intérmédiaires

DROP TABLE IF EXISTS Classe_Avion CASCADE;
CREATE TABLE IF NOT EXISTS Classe_Avion (
    
    nomClasse VARCHAR(80),
    idAvion SMALLINT,

    places SMALLINT NOT NULL CHECK (places > 0),
	
	CONSTRAINT PK_Classe_Avion PRIMARY KEY (nomClasse, idAvion)
);

DROP TABLE IF EXISTS Classe_Réservation CASCADE;
CREATE TABLE IF NOT EXISTS Classe_Réservation (

    nomClasse VARCHAR(80) NOT NULL,
    idRéservation SMALLINT,

    places SMALLINT NOT NULL CHECK (places > 0),
	
	CONSTRAINT PK_Classe_Réservation PRIMARY KEY (nomClasse, idRéservation)
);

DROP TABLE IF EXISTS Vol_Réservation CASCADE;
CREATE TABLE IF NOT EXISTS Vol_Réservation (

    idRéservation SMALLINT,
    idVol SMALLINT,
	
	CONSTRAINT PK_Vol_Réservation PRIMARY KEY (idRéservation, idVol)
);

DROP TABLE IF EXISTS Voyageur_Réservation CASCADE;
CREATE TABLE IF NOT EXISTS Voyageur_Réservation (

    idVoyageur SMALLINT,
    idRéservation SMALLINT,
	
	CONSTRAINT PK_Voyageur_Réservation PRIMARY KEY (idVoyageur, idRéservation)
);

