--Contraintes d'intégrité

--Disjointure des comptes

CREATE OR REPLACE FUNCTION Check_Compte_Disjoint() RETURNS TRIGGER
LANGUAGE plpgsql
AS
$BODY$
BEGIN
	IF (SELECT COUNT(mailCompte)
	   	FROM Client
	   	WHERE mailCompte = NEW.mailCompte) + (
			SELECT COUNT(mailCompte)
			FROM Administrateur
			WHERE mailCompte = NEW.mailCompte) > 1 THEN
		RAISE EXCEPTION 'Un compte ne peut pas être à la fois Client et Administrateur';
	END IF;
	RETURN NULL;
END;
$BODY$;

CREATE OR REPLACE TRIGGER Après_Insertion_Administrateur
AFTER INSERT ON Administrateur
FOR EACH ROW
EXECUTE FUNCTION Check_Compte_Disjoint();

CREATE OR REPLACE TRIGGER Après_Insertion_Client
AFTER INSERT ON Client
FOR EACH ROW
EXECUTE FUNCTION Check_Compte_Disjoint();

--Cohérence Vol_Réservation

CREATE OR REPLACE FUNCTION Check_Vol_Réservation() RETURNS TRIGGER
LANGUAGE plpgsql
AS
$BODY$
DECLARE
	t_row RECORD;
	firstLoop BOOLEAN := TRUE;
	aR Réservation.allerRetour%type;
	dernierPaysDépart Vol.codePaysAéroportDépart%type;
	dernièreVilleDépart Vol.nomVilleAéroportDépart%type;
	dernierAéroportDépart Vol.nomAéroportDépart%type;
	dernierPaysArrivée Vol.codePaysAéroportArrivée%type;
	dernièreVilleArrivée Vol.nomVilleAéroportArrivée%type;
	dernierAéroportArrivée Vol.nomAéroportArrivée%type;
	tempsDernierDépart Vol.dateEtHeureDeDépart%type;
	tempsDernièreArrivée Vol.dateEtHeureDArrivée%type;
	dernierVolID Vol.id%type;
BEGIN
	--Validation de la date et heure de réservation
	IF ((SELECT dateEtHeureDeDépart
		FROM Vol
		WHERE id = NEW.idVol) - INTERVAL '4 hours') < (
			SELECT dateEtHeureRéservation
			FROM Réservation
			WHERE id = NEW.idRéservation) THEN
		RAISE EXCEPTION 'Une réservation doit être faite au moins 4 heures avant le vol.';
	END IF;
	
	--Validation des bagages en soute et à main
	FOR t_row IN
		SELECT SUM(Réservation.nbBagagesEnSoute) * Vol.poidsMaxBagagesEnSoute AS poids,
			Avion.poidsMaxEnSoute AS poidsMax,
			SUM(Réservation.nbBagagesAMain) AS bagages,
			Avion.maxBagageAMain AS maxBagages,
			Vol.id AS VolID
		FROM Réservation
			INNER JOIN Vol_Réservation
				ON Vol_Réservation.idRéservation = Réservation.id
			INNER JOIN Vol
				ON Vol.id = Vol_Réservation.idVol
			INNER JOIN Avion
				ON Avion.id = Vol.idAvion
		WHERE Vol_Réservation.idVol = ANY (SELECT idVol
									  FROM Vol_Réservation
									  WHERE idRéservation = NEW.idRéservation)
		GROUP BY Vol.id, Avion.poidsMaxEnSoute, Avion.maxBagageAMain
	LOOP
		IF (t_row.poids > t_row.poidsMax) THEN
			RAISE EXCEPTION 'Il n y a plus assez de place en soute pour le vol %.', t_row.VolID;
		END IF;
		IF (t_row.bagages > t_row.maxBagages) THEN
			RAISE EXCEPTION 'Il n y a plus de place pour des bagages à main dans le vol %.', t_row.VolID;
		END IF;
	END LOOP;
	
	--Validité des escales
	SELECT allerRetour FROM Réservation INTO aR WHERE id = NEW.idRéservation;
	FOR t_row IN
		SELECT codePaysAéroportDépart AS paysDépart,
			nomVilleAéroportDépart AS villeDépart,
			nomAéroportDépart AS aéroportDépart,
			codePaysAéroportArrivée AS paysArrivée,
			nomVilleAéroportArrivée AS villeArrivée,
			nomAéroportArrivée AS aéroportArrivée,
			dateEtHeureDeDépart AS tempsDépart,
			dateEtHeureDArrivée AS tempsArrivée,
			id AS VolID
		FROM Vol
		WHERE id = ANY (SELECT idVol FROM Vol_Réservation WHERE idRéservation = NEW.idRéservation)
		ORDER BY dateEtHeureDeDépart
	LOOP
		IF (firstLoop = FALSE) THEN
			IF ((dernierPaysArrivée, dernièreVilleArrivée, dernierAéroportArrivée) <>
				(t_row.paysDépart, t_row.villeDépart, t_row.aéroportDépart)) THEN
				IF (aR = TRUE) THEN
					aR = FALSE;
				ELSE
					RAISE EXCEPTION 'Escale invalide pour les vols (%, %) qui ne sont pas liées aux mêmes aéroports.', dernierVolID, t_row.VolID;
				END IF;
			END IF;
			
			IF (tempsDernièreArrivée > t_row.tempsDépart) THEN
				RAISE EXCEPTION 'Escale invalide pour les vols (%, %) qui ont lieu au même temps.', dernierVolID, t_row.VolID;
			END IF;
		END IF;
		
		dernierPaysDépart = t_row.paysDépart;
		dernièreVilleDépart = t_row.villeDépart;
		dernierAéroportDépart = t_row.aéroportDépart;
		dernierPaysArrivée = t_row.paysArrivée;
		dernièreVilleArrivée = t_row.villeArrivée;
		dernierAéroportArrivée = t_row.aéroportArrivée;
		tempsDernierDépart = t_row.tempsDépart;
		tempsDernièreArrivée = t_row.tempsArrivée;
		dernierVolID = t_row.VolID;
		firstLoop = FALSE;
		
	END LOOP;
	
	RETURN NULL;
END;
$BODY$;

CREATE OR REPLACE TRIGGER Après_Insertion_Vol_Réservation
AFTER INSERT ON Vol_Réservation
FOR EACH ROW
EXECUTE FUNCTION Check_Vol_Réservation();

--Cohérence Classe_Réservation

CREATE OR REPLACE FUNCTION Check_Classe_Réservation() RETURNS TRIGGER
LANGUAGE plpgsql
AS
$BODY$
DECLARE
	t_row RECORD;
BEGIN
	IF (SELECT SUM(places)
	   	FROM Classe_Réservation
	   	WHERE idRéservation = NEW.idRéservation) > (
			SELECT COUNT(idVoyageur)
			FROM Voyageur_Réservation
			WHERE idRéservation = NEW.idRéservation) THEN
		RAISE EXCEPTION 'Nombre de places réservées supérieur au nombre de voyageurs.';
	END IF;
	
	FOR t_row IN
		SELECT Classe_Réservation.nomClasse,
			SUM(Classe_Réservation.places) AS sumPlaces,
			Classe_Avion.places AS maxPlaces,
			Classe_Avion.idAvion AS AvionID
		FROM Classe_Réservation
			INNER JOIN Classe_Avion
				ON Classe_Réservation.nomClasse = Classe_Avion.nomClasse
		WHERE Classe_Réservation.nomClasse = NEW.nomClasse
			AND Classe_Réservation.idRéservation = ANY (
				SELECT idRéservation
				FROM Vol_Réservation
				WHERE idVol = ANY (
					SELECT idVol
					FROM Vol_Réservation
					WHERE idRéservation = NEW.idRéservation))
			AND Classe_Avion.idAvion = ANY (
				SELECT idAvion
				FROM Vol
				WHERE id = ANY (
					SELECT idVol
					FROM Vol_Réservation
					WHERE idRéservation = NEW.idRéservation))
		GROUP BY Classe_Réservation.nomClasse, Classe_Avion.places, Classe_Avion.idAvion
	LOOP
		IF (t_row.sumPlaces > t_row.maxPlaces) THEN
			RAISE EXCEPTION 'Il n y a plus assez de places dans l avion %.', t_row.AvionID;
		END IF;
	END LOOP;
	RETURN NULL;
END;
$BODY$;

CREATE OR REPLACE TRIGGER Après_Insertion_Classe_Réservation
AFTER INSERT ON Classe_Réservation
FOR EACH ROW
EXECUTE FUNCTION Check_Classe_Réservation();

--Cohérence Voyageur_Réservation

CREATE OR REPLACE FUNCTION Check_Voyageur_Réservation() RETURNS TRIGGER
LANGUAGE plpgsql
AS
$BODY$
BEGIN
	IF ((SELECT dateDeNaissance 
		FROM Voyageur 
		WHERE id = NEW.idVoyageur) + INTERVAL '2 years') > ANY (
			SELECT dateEtHeureDeDépart 
			FROM Vol 
			   	INNER JOIN Vol_Réservation 
					ON Vol.id = Vol_Réservation.idVol
			WHERE Vol_Réservation.idRéservation = NEW.idRéservation) THEN
			
		RAISE EXCEPTION 'Un voyageur doit avoir au moins 2 ans.';
	END IF;
	RETURN NULL;
END;
$BODY$;

CREATE OR REPLACE TRIGGER Après_Insertion_Voyageur_Réservation
AFTER INSERT ON Voyageur_Réservation
FOR EACH ROW
EXECUTE FUNCTION Check_Voyageur_Réservation();

