DROP VIEW IF EXISTS vVols;
CREATE OR REPLACE VIEW vVols
AS
SELECT Vol.id AS "IDVol",
	Vol.nomAéroportDépart AS "AéroportDeDépart",
	AéroportDépart.diminutif AS "DiminutifDeLAéroportDeDépart",
	Vol.nomVilleAéroportDépart AS "VilleDeDépart",
	PaysDépart.nom AS "PaysDeDépart",
	Vol.nomAéroportArrivée AS "AéroportDArrivée",
	AéroportArrivée.diminutif AS "DiminutifDeLAéroportDArrivée",
	Vol.nomVilleAéroportArrivée AS "VilleDArrivée",
	PaysArrivée.nom AS "PaysDArrivée",
  	Vol.dateEtHeureDeDépart AS "TempsDeDépart",
  	Vol.dateEtHeureDArrivée AS "TempsDArrivée",
  	Vol.prix AS "PrixDuVol",
	COALESCE(SUM (Réservation.nbBagagesAMain), 0) AS "NombreDeBagagesAMainDéjàRéservés",
	Avion.maxBagageAMain AS "MaxBagagesAMain",
	COALESCE(SUM (Réservation.nbBagagesEnSoute), 0) * Vol.poidsmaxBagagesEnSoute AS "PoidsDesBagagesEnSouteDéjàRéservés",
	Vol.poidsMaxBagagesEnSoute AS "PoidsMaxDeBagageEnSoute",
	Avion.poidsMaxEnSoute AS "PoidsMaxEnSoute",
  	Avion.nomCompagnie AS "CompagnieAérienne",
	Classe_Avion.nomClasse AS "ClasseDeVol",
	Classe.supplément AS "Supplément",
	COALESCE(SUM (Classe_Réservation.places), 0) AS "NombreDePlacesDéjàRéservées",
	Classe_Avion.places AS "NombreDePlacesTotales"
FROM Vol
	INNER JOIN Aéroport AS AéroportDépart
		ON (AéroportDépart.nom, AéroportDépart.nomVille, AéroportDépart.codePays) = (Vol.nomAéroportDépart, Vol.nomVilleAéroportDépart, Vol.codePaysAéroportDépart)
	INNER JOIN Pays AS PaysDépart
		ON Vol.codePaysAéroportDépart = PaysDépart.codeAlpha3
	INNER JOIN Aéroport AS AéroportArrivée
		ON (AéroportArrivée.nom, AéroportArrivée.nomVille, AéroportArrivée.codePays) = (Vol.nomAéroportArrivée, Vol.nomVilleAéroportArrivée, Vol.codePaysAéroportArrivée)
	INNER JOIN Pays AS PaysArrivée
		ON Vol.codePaysAéroportArrivée = PaysArrivée.codeAlpha3
  	INNER JOIN Avion
    	ON Vol.idAvion = Avion.id
  	LEFT JOIN Classe_Avion
    	ON Avion.id = Classe_Avion.idAvion
	LEFT JOIN Classe
		ON Classe.nom = Classe_Avion.nomClasse
	LEFT JOIN Vol_Réservation
		ON Vol_Réservation.idVol = Vol.id
	LEFT JOIN Réservation
		ON Vol_Réservation.idRéservation = Réservation.id
	LEFT JOIN Classe_Réservation
		ON Classe_Réservation.idRéservation = Réservation.id
GROUP BY Vol.id,
	AéroportDépart.diminutif,
	AéroportArrivée.diminutif,
	PaysDépart.nom,
	PaysArrivée.nom,
	Avion.maxBagageAMain,
	Avion.poidsMaxEnSoute,
	Avion.nomCompagnie,
	Classe_Avion.nomClasse,
	Classe_Avion.places,
	Classe.supplément;
	
